const authReducer = (authObj = INITIAL_STATE, action) => {
  switch(action.type) {
    //must return a new object/array (reference must be different)
    case SIGN_IN:
      return {...authObj, isSignedIn: true, userId: action.payload};
    case SIGN_OUT:
      //for arrays
      return [...authObj, {value: 1}];
    default:
      return authObj;
  }
}
export default authReducer;
