const repositoReducer = (repositoObj = INITIAL_STATE, action) => {
  switch(action.type) {
    //must return a new object/array (reference must be different)
    case SIGN_IN:
      return {...repositoObj, isSignedIn: true, userId: action.payload};
    case SIGN_OUT:
      //for arrays
      return [...repositoObj, {value: 1}];
    default:
      return repositoObj;
  }
}
export default repositoReducer;
