import { CLICK_SKILL, ADD_REPO, SET_SECTIONS_TO_SKIP_ANIMATION } from '../actions/types';

const INITIAL_STATE = {
  clickedSkill: null,
  reposToDisplay: [],
  sectionsToSkipAnimation: [],
}



const sectionsToSkipAnimationReducer = (state = INITIAL_STATE, action) => {
  switch (action.type) {
    case SET_SECTIONS_TO_SKIP_ANIMATION:
      return action.payload;
    case ADD_REPO:
      if (action.payload?.length === 0) return [];
      return [...state, action.payload];
    case CLICK_SKILL:
        return action.payload;
    default:
      return state;
  }
}

export default reposToDisplayReducer;
