import React from 'react';
import ReactDOM from 'react-dom';

class NavToggler extends React.Component {
  handleOnClick = (e) => {
    console.log('e.target =', e.target);
    e.target.classList?.toggle('header-toggler--active')
  }

  render () {
    return (
      ReactDOM.createPortal(
        <div onClick={this.handleOnClick} className='header-toggler'>
          <svg className="header-toggler__svg">
            <use xlinkHref="sprite.svg#icon-angle-double-down"></use>
          </svg>
        </div>
      ,
        document.body.querySelector('.')
      )
    );
  }
}

export default NavToggler;
