import React from 'react';
import { Link } from 'react-router-dom';

class NavItem extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      //initializing props to keep track of here
    };
    //you have to create a ref for each element you are planning to interact with in the DOM for each component
    this.imageRef = React.createRef();
  }
  render() {
    const { onMouseEnter, to, label } = this.props;
    return (
      <li onMouseEnter={onMouseEnter} className="navbar__item">
        <Link className="navbar__link" to={to}>{label}</Link>
      </li>
    );
  }
}

export default NavItem;
