const repositReducer = (repositObj = INITIAL_STATE, action) => {
  switch(action.type) {
    //must return a new object/array (reference must be different)
    case SIGN_IN:
      return {...repositObj, isSignedIn: true, userId: action.payload};
    case SIGN_OUT:
      //for arrays
      return [...repositObj, {value: 1}];
    default:
      return repositObj;
  }
}
export default repositReducer;
