const repReducer = (repObj = INITIAL_STATE, action) => {
  switch(action.type) {
    //must return a new object/array (reference must be different)
    case SIGN_IN:
      return {...repObj, isSignedIn: true, userId: action.payload};
    case SIGN_OUT:
      //for arrays
      return [...repObj, {value: 1}];
    default:
      return repObj;
  }
}
export default repReducer;
