import { ADD_REPO } from '../actions/types';

const reposToDisplayReducer = (state = [], action) => {
  switch (action.type) {
      case ADD_REPO:
          return [...state, action.payload];
      default:
          return state;
  }
}

export default reposToDisplayReducer;
