import React from 'react';
import ReactDOM from 'react-dom';
import { connect } from 'react-redux';
import { Link } from 'react-router-dom';
import { capitalize } from '../../helpers';

class PageNav extends React.Component {
  static cssClass = 'page-nav';
  static previousPage = null;

  componentDidMount () {
    debugger;
    const currentPage = this.props.match?.non;
    if (!PageNav.previousPage || PageNav.previousPage !== currentPage) PageNav.previousPage = currentPage;
  }

  renderSections = () => {
    let sectionNames = [];
    const sections = document.querySelectorAll('[data-section]');
    // debugger
    for (let i = 0; i < sections.length; i++) {
      const section = sections[i];
      const capitalized = capitalize(section.dataset.section);
      debugger
      sectionNames.push(capitalized?.replace('-', ' '))
    }

    return sectionNames.map((sectionName, index, array) => {
      return (
        <span className={`${PageNav.cssClass}__section ${PageNav.cssClass}__section-${sectionName}`}>
          {sectionName}
          {index < (array.length - 1) ?
            '/'  
          : 
            null
          }
        </span>
      );
    });
  }

  render() {
    console.log('this.props.sectionsToAddToPageNav =', this.props.sectionsToAddToPageNav);
    return (
      ReactDOM.createPortal(
        //The idea behind this component is to have a nav element that has quick links  to the sections of each page
        <div className={PageNav.cssClass}>
          {this.renderSections()}
        </div>
      ,
        document.querySelector('.header__inner')
      )
    );
  }
}

const mapStateToProps = (state, ownProps) => {
  return { 
    sectionsToAddToPageNav: state.sectionsToAddToPageNav,
  }
}

export default connect(mapStateToProps, {

})(PageNav);
