const reposReducer = (reposObj = INITIAL_STATE, action) => {
  switch(action.type) {
    //must return a new object/array (reference must be different)
    case SIGN_IN:
      return {...reposObj, isSignedIn: true, userId: action.payload};
    case SIGN_OUT:
      //for arrays
      return [...reposObj, {value: 1}];
    default:
      return reposObj;
  }
}
export default reposReducer;
