  render() {
    return (
      <section className="home">
        <BackgroundVideo/>

        <div className="home__portrait">
          {/* <img src='../../img/self-2.png' alt="Adam Major"/> */}
        </div>
      </section>
    );
  }
}

const mapStateToProps = (state, ownProps) => {
  return {
    repos: state.repos,
  }
}

export default connect(mapStateToProps, {
  getRepositories,
})(Home);
