import {combineReducers} from "redux";

const isAnimatingReducer = (state = false, action) => {
    switch (action.payload) {
        case "IS_ANIMATING":
            return action.payload;
        case "IS_NOT_ANIMATING":
            return false;
        default:
            return state;
    }
}

export default combineReducers({
    isAnimating: isAnimatingReducer,
})
