import { GET_REPOSITORIES } from '../actions/types'

const repositoriesReducer = (repositoriesObj = INITIAL_STATE, action) => {
  switch(action.type) {
    //must return a new object/array (reference must be different)
    case GET_REPOSITORIES:
      return {...repositoriesObj, isSignedIn: true, userId: action.payload};
      //for arrays
      return [...repositoriesObj, {value: 1}];
    default:
      return repositoriesObj;
  }
}
export default repositoriesReducer;
