import React from 'react';

class SkillsItemSection extends React.Component {
  onTitleClick = (e) => {
    e.target?.classList?.toggle('skills__section--open');
    this.setOpeningTimers();
  }

  setOpeningTimers = () => {
    const items = document.querySelectorAll('.skills__percent-outer');
    let timeOutDifferential = 50;
    for (let i = 0; i < items.length; i++) {
      const item = items[i];
      setTimeout(() => {
        item.style.width = '100%';
      }, timeOutDifferential * i);

    }
  }

  render() {
    const { children, title } = this.props;
    return (
      <React.Fragment>
        <div onClick={this.onTitleClick} className="skills__section-title">{title}:</div>
        <div className="skills__section-content">
          {children}
        </div>
      </React.Fragment>
    );
  }
}

export default SkillsItemSection;
