import { SET_IS_MOBILE } from '../actions/types';

const isMobileReducer = (state = null, payload) => {
  switch (payload.type) {
    case SET_IS_MOBILE:

      return payload.value;      
    default:
      return state;
  }
}

export default isMobileReducer;
